(430, 'poll_template_voteheader', '<p style=\\"text-align: center;\\"><strong>%POLL_QUESTION%</strong></p><div id=\\"polls-%POLL_ID%-ans\\" class=\\"wp-polls-ans\\"><ul class=\\"wp-polls-ul\\">', 'yes'),
(431, 'poll_template_votebody', '<li><input type=\\"%POLL_CHECKBOX_RADIO%\\" id=\\"poll-answer-%POLL_ANSWER_ID%\\" name=\\"poll_%POLL_ID%\\" value=\\"%POLL_ANSWER_ID%\\" /> <label for=\\"poll-answer-%POLL_ANSWER_ID%\\">%POLL_ANSWER%</label></li>', 'yes'),
(432, 'poll_template_votefooter', '</ul><p style=\\"text-align: center;\\"><input type=\\"button\\" name=\\"vote\\" value=\\"   Vote   \\" class=\\"Buttons\\" onclick=\\"poll_vote(%POLL_ID%);\\" /></p><p style=\\"text-align: center;\\"><a href=\\"#ViewPollResults\\" onclick=\\"poll_result(%POLL_ID%); return false;\\" title=\\"Parcial\\">Parcial</a></p></div>', 'yes'),
(433, 'poll_template_resultheader', '<p style=\\"text-align: center;\\"><strong>%POLL_QUESTION%</strong></p><div id=\\"polls-%POLL_ID%-ans\\" class=\\"wp-polls-ans\\"><ul class=\\"wp-polls-ul\\">', 'yes'),
(434, 'poll_template_resultbody', '<li>%POLL_ANSWER% <small>(%POLL_ANSWER_PERCENTAGE%%, %POLL_ANSWER_VOTES% Votes)</small><div class=\\"pollbar\\" style=\\"width: %POLL_ANSWER_IMAGEWIDTH%%;\\" title=\\"%POLL_ANSWER_TEXT% (%POLL_ANSWER_PERCENTAGE%% | %POLL_ANSWER_VOTES% Votes)\\"></div></li>', 'yes'),
(435, 'poll_template_resultbody2', '<li><strong><i>%POLL_ANSWER% <small>(%POLL_ANSWER_PERCENTAGE%%, %POLL_ANSWER_VOTES% Votes)</small></i></strong><div class=\\"pollbar\\" style=\\"width: %POLL_ANSWER_IMAGEWIDTH%%;\\" title=\\"You Have Voted For This Choice - %POLL_ANSWER_TEXT% (%POLL_ANSWER_PERCENTAGE%% | %POLL_ANSWER_VOTES% Votes)\\"></div></li>', 'yes'),
(436, 'poll_template_resultfooter', '</ul><p style=\\"text-align: center;\\">Total Voters: <strong>%POLL_TOTALVOTERS%</strong></p></div>', 'yes'),
(437, 'poll_template_resultfooter2', '</ul><p style=\\"text-align: center;\\">Total Voters: <strong>%POLL_TOTALVOTERS%</strong></p><p style=\\"text-align: center;\\"><a href=\\"#VotePoll\\" onclick=\\"poll_booth(%POLL_ID%); return false;\\" title=\\"Vote For This Poll\\">Vote</a></p></div>', 'yes'),
